format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<span></span>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set pitems=[] %}
{% set a= ((doc.items|length)%17)|int %}
{% set b= ((doc.items|length)%17)|int %}


{% for i in range(a) %}
    {% set x=[] %}
    {% set _ = x.append(doc.items[i*17:i*17 + 17]) %}
    {% set _ = pitems.append(x) %}
{% endfor %}

{% if(b>0) %}
    {% set _ = pitems.append([doc.items[a*17:a*17 + b]]) %}
{% endif %}

{% set serial=[0] %}

{%- set serial=[1] -%}


{% for zz in pitems %}
{% for items in zz %}




<div style="border:1px solid black;padding:5px;height:825px">
    <style>
table, th, td {
  border: 1px solid black;
}
table {
  border-collapse: collapse;
  width: 1%;
}

th {
  height: 1px;
}

</style>
<div id="orange" style="position:relative;float:right;">
<table>
  <tr>
    <th><center>PURCHASE ORDER N0</center></th>
    <th><center>Date</center></th>
    <th><center>Required By</center></th>
  </tr>
  <tr>
    <td><center>{{doc.name}}</center></td>
     <td><center>{{doc.transaction_date}}</center></td>
     <td><center>{{doc.schedule_date}}</center></td>
  </tr>
</table>
</div>

<br>
<br>
<br>

<d><b style="font-size:130%;">To. M/s : {{doc.supplier_name}}</b></d>
<hr>

<style>
table {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

td, th {
  border: 1px solid #dddddd;
  text-align: left;
  padding: 8px;
}

tr:nth-child(even) {
  background-color: #dddddd;
}
table, th, td {
  border:1px solid black;
  border-collapse: collapse;
}
</style>

<h4>Sir/Madam</h4>
<p style="font-size:110%;">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;Kindly delivery the following items through the bearer of this Purchase Memo</p>
<hr>

<table>
  <tr>
     <th><center>S.No</center></th> 
    <th><center>Particulars</center></th>
    <th><center>Quantity</center></th>
  </tr>

  {%- for row in items -%}
  <tr>
    <td><center>{{serial|length}}</center></td>
    <td>{{row.item_code}}</td>
    <td>{{row.qty}}</td>
  </tr>
  {%- set _ =serial.append(1) -%}
  {%- endfor -%}

</table>


</div>
<br>
{% endfor %}
{% endfor %}"}]
