css:
.print-format {
    margin-left: 5mm; 
    margin-right: 5mm; 
    margin-top: 5mm;
    margin-bottom: 5mm;
   
}

.everything{
    page-break-inside: avoid !important;
}

.main{
    page-break-inside: avoid;
}

.letter_head{
    border:1px solid black;
    border-bottom:0px;
}

.box1{
    float:left;
    overflow:hidden;
    padding-left:3px;
    padding-top:3px;
    padding-bottom:3px;
    width:60%;
}

.box2{
    border-bottom:1px solid black;
    border-left:1px solid black;
    padding-top:3px;
    padding-bottom:3px;
    width:40%;
    float:right;
    overflow: hidden;
    padding-bottom: 32768px;
    margin-bottom: -32768px;
    padding-top: 32768px;
    margin-top: -32768px
}

.box2i{
    border-bottom:1px solid black;
    /*border-left:1px solid black;*/
    width:100%;
    padding-left:3px;
    padding-top:3px;
    padding-bottom:3px;
}

.box1and2{
    border:1px solid black;
    width:100%;
    overflow:hidden;
    padding-left:3px;
    border-bottom:0px;
}

.table{
    width:100%;
    padding-top:0px;
    margin-top:0px;
    padding-bottom:0 !important;
}

.table th{
    text-align:center;
}

.actualtable{
    width:100%;
    border:1px solid black;
    width:100%;
    margin-bottom:0 !important;
    height:100%;
}

.actualtable td{
    border-left:1px solid black;
}

.actualtable th{
    border-left:1px solid black;
}

.tax_table{
    float: left;
    width:60%;
    
}

.top_box{
    border:1px solid black;
    page-break-inside: avoid;
    overflow:hidden;
}

.tax_table table{
    width:100%;
}

.tax_table tr{
    border-bottom:1px solid black;
}

.tax_table td{
    border-right:1px solid black;
}

.tax_table tr{
    border-right:1px solid black;
}

.tax_table_2{
    width:60%;
    float:right;
}

.tax_table_2 table{
    height:100%;
    width:100%;
}

.tax_table_2 tr{
    border:0px;
    line-height:0.7 !important;
}

.in_words{
    width:100%;
}

.in_words table{
    width:100%;
}

.taxable_amount{
    width:40%;
    float: right;
}

.taxable_amount table{
    width:100%;
    text-align:right;
}

.taxable_amount tr{
    line-height:0.5 !important;
}

.in_words{
    border-top:1px solid black;
    overflow: hidden;
}

.sign{
    width:100%
}

.sign table{
    width:100%;
    border-bottom:1px solid black;
    border-left:1px solid black;
}

.sign td{
    border-right:1px solid black;
}

/*Alignments inside table*/

#left{
    text-align:left;
}

#center{
    text-align:center;
}

#right{
    text-align:right;
}

